import React, { useState } from "react";
import uuid from "react-uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxiosCard from "./hooks/useAxiosCard.js";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const list = useAxiosCard("//deckofcardsapi.com/api/deck/new/draw/");
  const [cards, setCards] = useState(list.response);
  const addCard = async () => {
    const response = useAxiosCard("//deckofcardsapi.com/api/deck/new/draw/");
    console.log(response);

    setCards((cards) => [...cards, { ...response.data, id: uuid() }]);
  };
  if (list.loading) {
    return <div>Loading.....</div>;
  } else if (list.error != null) {
    return <div>sorry error</div>;
  } else {
    const res = list.response.data;
    console.log();
    return (
      <div className="PlayingCardList">
        <h3>Pick a card, any card!</h3>
        <div>
          <button onClick={addCard}>Add a playing card!</button>
        </div>
        <div className="PlayingCardList-card-area">
          {cards.map((cardData) => (
            <PlayingCard key={cardData.id} front={cardData.cards[0].image} />
          ))}
        </div>
      </div>
    );
  }
}

CardTable.defaultProps = {};

export default CardTable;
