import React, { useState } from "react";
import uuid from "react-uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxiosCard from "./hooks/useAxiosCard.js";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  // const cards = useAxiosCard("//deckofcardsapi.com/api/deck/new/draw/");
  const [cards, setCards] = useAxiosCard(
    "//deckofcardsapi.com/api/deck/new/draw/"
  );
  // const [cards, setCards] = useState([]);
  const addCard = async () => {
    const response = await axios.get(
      "https://deckofcardsapi.com/api/deck/new/draw/"
    );
    setCards((cards) => [...cards, { ...response.data, id: uuid() }]);
  };
  if (cards.loading) {
    return <div>Loading.....</div>;
  } else if (cards.error != null) {
    return <div>sorry error</div>;
  } else {
    const res = cards.response.data.cards;
    console.log(res);
    return (
      <div className="PlayingCardList">
        <h3>Pick a card, any card!</h3>
        <div>
          <button onClick={setCards}>Add a playing card!</button>
        </div>
        <div className="PlayingCardList-card-area">
          {cards.map((cardData) => (
            <PlayingCard key={cardData.id} front={cardData.cards[0].image} />
          ))}
        </div>
      </div>
    );
  }
}

CardTable.defaultProps = {};

export default CardTable;
