import React, { useEffect, useState } from "react";
import uuid from "react-uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxiosCard from "./hooks/useAxiosCard.js";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  let start = "//deckofcardsapi.com/api/deck/new/draw/";
  const [url, setUrl] = useState(start);
  const [list, setList] = useAxiosCard(start);
  console.log(list);

  const [cards, setCards] = useState([]);
  const addCard = () => {
    setUrl((url = "//deckofcardsapi.com/api/deck/new/draw/"));
  };
  useEffect(() => {
    setList((list) => useAxiosCard(url));
  }, url);

  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={addCard}>Add a playing card!</button>
      </div>
      <div className="PlayingCardList-card-area">
        {cards.map((cardData) => (
          <PlayingCard key={cardData.id} front={cardData.cards[0].image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;

