import React, { useState } from "react";
import uuid from "react-uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxiosCard from "./hooks/useAxiosCard.js";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const list = useAxiosCard("//deckofcardsapi.com/api/deck/new/draw/");
  const [cards, setCards] = useState(list);
  const res = cards.response.data;
  setCards((cards) => [...cards, { ...res.data, id: uuid() }]);
  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={useAxiosCard}>Add a playing card!</button>
      </div>
      <div className="PlayingCardList-card-area">
        {cards.map((cardData) => (
          <PlayingCard key={cardData.id} front={cardData.cards[0].image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;
