import React, { useEffect, useState } from "react";
import uuid from "react-uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxiosCard from "./hooks/useAxiosCard.js";

/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const [url, setUrl] = useState(null);
  let cards = useAxiosCard(url);
  const [list, setList] = useState(cards.response);
  console.log(list);
  const addCard = () => {
    setUrl((url) => "");
    setUrl((url) => "https://deckofcardsapi.com/api/deck/new/draw/");
    setList((list) => [...list, { ...cards, id: uuid() }]);
  };

  return (
    <div className="PlayingCardList">
      <h3>Pick a card, any card!</h3>
      <div>
        <button onClick={addCard}>Add a playing card!</button>
      </div>
      <div className="PlayingCardList-card-area">
        {list.map((cardData) => (
          <PlayingCard key={cardData.id} front={cardData.response[0].image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;

